'use strict'

const { wrapCoreRule } = require('../utils')

module.exports = wrapCoreRule('comma-style', {
  create(_context, { coreHandlers }) {
    return {
      VSlotScopeExpression(node) {
        if (coreHandlers.FunctionExpression) {
          coreHandlers.FunctionExpression(node)
        }
      }
    }
  }
})
