import { getVisitor } from "./visit";

export default function (context) {
  const plugin = {
    visitor: getVisitor(context),
  };

  const version = context && context.version;

  if (version && parseInt(version, 10) >= 7) {
    plugin.name = "regenerator-transform";
  }

  return plugin;
}
