/* @flow */

import { addAttr } from 'compiler/helpers'

export function postTransformComponentRoot (
  el: ASTElement,
  options: WeexCompilerOptions
) {
  if (!el.parent) {
    addAttr(el, '@isComponentRoot', 'true')
    addAttr(el, '@templateId', '_uid')
    addAttr(el, '@componentProps', '$props || {}')
  }
}
