'use strict';

const signals = ['SIGINT', 'SIGTERM'];

function setupExitSignals(serverData) {
  signals.forEach((signal) => {
    process.on(signal, () => {
      if (serverData && serverData.server) {
        serverData.server.close(() => {
          process.exit();
        });
      } else {
        process.exit();
      }
    });
  });
}

module.exports = setupExitSignals;
